INSERT INTO `kbp_kb_entry` (`id`, `category_id`, `author_id`, `updater_id`, `title`, `body`, `body_index`, `url_title`, `meta_keywords`, `meta_description`, `entry_type`, `external_link`, `date_updated`, `date_posted`, `date_commented`, `history_comment`, `hits`, `sort_order`, `private`, `active`) VALUES
(1, 1, 1, 1, 'Getting Started', '<p>This article explains how you can create an article, news, user in KBPublisher.</p>\r\n\r\n<p>If you not signed in yet,&nbsp;Click&nbsp;<strong>Sign in</strong>&nbsp;at&nbsp;the top right corner, fill the fields "Username" and "Password" and click&nbsp;<strong>OK.</strong><br />\r\n&nbsp;</p>\r\n\r\n<h3 class="lineTitle">Create an Article</h3>\r\n\r\n<ol>\r\n	<li>Click <svg class="tmIcon" fill="#999" viewbox="0 0 24 24" width="18" xmlns="http://www.w3.org/2000/svg"><path d="M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0-2c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm6 13h-5v5h-2v-5h-5v-2h5v-5h2v5h5v2z"></path></svg>&nbsp;at&nbsp;the top right corner&nbsp;and choose&nbsp;<strong>Add Article Here</strong> from the popup&nbsp;menu.</li>\r\n	<li>Fill "Title",&nbsp;"Category" and "Article" fields and click <strong>Save.</strong>&nbsp;</li>\r\n	<li>See&nbsp;<a href="http://www.kbpublisher.com/kb/the-article-input-screen-explained_40.html">The Article Input Screen Explained</a>&nbsp;article for details.</li>\r\n</ol>\r\n\r\n<h3 class="lineTitle">Create a News</h3>\r\n\r\n<ol>\r\n	<li>Click <svg class="tmIcon" fill="#999" viewbox="0 0 24 24" width="18" xmlns="http://www.w3.org/2000/svg"><path d="M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0-2c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm6 13h-5v5h-2v-5h-5v-2h5v-5h2v5h5v2z"></path></svg>&nbsp;at&nbsp;the top right corner&nbsp;and choose <strong>Add News</strong> from the popup&nbsp;menu.</li>\r\n	<li>Fill "Date", "Title" and "Body" fields and click&nbsp;<strong>Save.</strong></li>\r\n	<li>See&nbsp;<a href="http://www.kbpublisher.com/kb/the-news-input-screen-explained_242.html">The News Input Screen Explained</a>&nbsp;article for details.</li>\r\n</ol>\r\n\r\n<h3 class="lineTitle">Add a File</h3>\r\n\r\n<ol>\r\n	<li>Click <svg class="tmIcon" fill="#999" viewbox="0 0 24 24" width="18" xmlns="http://www.w3.org/2000/svg"><path d="M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0-2c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm6 13h-5v5h-2v-5h-5v-2h5v-5h2v5h5v2z"></path></svg>&nbsp;at&nbsp;the top right corner&nbsp;and choose <strong>Add File</strong> from the popup&nbsp;menu.</li>\r\n	<li>Fill "File" (choose&nbsp;file from disk),&nbsp;"Category" and click&nbsp;<strong>Save</strong></li>\r\n	<li>See&nbsp;<a href="http://www.kbpublisher.com/kb/add-a-file_65.html">Add a File</a>&nbsp;article for details</li>\r\n</ol>\r\n\r\n<h3 class="lineTitle">Add a User</h3>\r\n\r\n<ol>\r\n	<li>Click <svg class="tmIcon" fill="#999" viewbox="0 0 24 24" width="18" xmlns="http://www.w3.org/2000/svg"><path d="M24 14.187v-4.374c-2.148-.766-2.726-.802-3.027-1.529-.303-.729.083-1.169 1.059-3.223l-3.093-3.093c-2.026.963-2.488 1.364-3.224 1.059-.727-.302-.768-.889-1.527-3.027h-4.375c-.764 2.144-.8 2.725-1.529 3.027-.752.313-1.203-.1-3.223-1.059l-3.093 3.093c.977 2.055 1.362 2.493 1.059 3.224-.302.727-.881.764-3.027 1.528v4.375c2.139.76 2.725.8 3.027 1.528.304.734-.081 1.167-1.059 3.223l3.093 3.093c1.999-.95 2.47-1.373 3.223-1.059.728.302.764.88 1.529 3.027h4.374c.758-2.131.799-2.723 1.537-3.031.745-.308 1.186.099 3.215 1.062l3.093-3.093c-.975-2.05-1.362-2.492-1.059-3.223.3-.726.88-.763 3.027-1.528zm-4.875.764c-.577 1.394-.068 2.458.488 3.578l-1.084 1.084c-1.093-.543-2.161-1.076-3.573-.49-1.396.581-1.79 1.693-2.188 2.877h-1.534c-.398-1.185-.791-2.297-2.183-2.875-1.419-.588-2.507-.045-3.579.488l-1.083-1.084c.557-1.118 1.066-2.18.487-3.58-.579-1.391-1.691-1.784-2.876-2.182v-1.533c1.185-.398 2.297-.791 2.875-2.184.578-1.394.068-2.459-.488-3.579l1.084-1.084c1.082.538 2.162 1.077 3.58.488 1.392-.577 1.785-1.69 2.183-2.875h1.534c.398 1.185.792 2.297 2.184 2.875 1.419.588 2.506.045 3.579-.488l1.084 1.084c-.556 1.121-1.065 2.187-.488 3.58.577 1.391 1.689 1.784 2.875 2.183v1.534c-1.188.398-2.302.791-2.877 2.183zm-7.125-5.951c1.654 0 3 1.346 3 3s-1.346 3-3 3-3-1.346-3-3 1.346-3 3-3zm0-2c-2.762 0-5 2.238-5 5s2.238 5 5 5 5-2.238 5-5-2.238-5-5-5z"></path></svg> at&nbsp;the top right corner.</li>\r\n	<li>Go to&nbsp;<strong>Users</strong>&nbsp;and then click&nbsp;<strong>Add New</strong></li>\r\n	<li>Fill required fields (marked with *) and click&nbsp;<strong>Save.</strong>&nbsp;</li>\r\n	<li>See&nbsp;<a href="http://www.kbpublisher.com/kb/the-user-input-screen-explained_73.html">The User Input Screen Explained</a>&nbsp;article for details.</li>\r\n</ol>\r\n', 'This article explains how you can create an article, news, user in KBPublisher. If you not signed in yet, Click Sign in at the top right corner, fill the fields "Username" and "Password" and click OK. Create an Article Click at the top right corner and choose Add Article Here from the popup menu. Fill "Title", "Category" and "Article" fields and click Save. See The Article Input Screen Explained article for details. Create a News Click at the top right corner and choose Add News from the popup menu. Fill "Date", "Title" and "Body" fields and click Save. See The News Input Screen Explained article for details. Add a File Click at the top right corner and choose Add File from the popup menu. Fill "File" (choose file from disk), "Category" and click Save See Add a File article for details Add a User Click at the top right corner. Go to Users and then click Add New Fill required fields (marked with *) and click Save. See The User Input Screen Explained article for details.', '', '', '', 0, '', '2017-04-12 13:13:27', '2017-04-12 16:46:27', '0000-00-00 00:00:00', '', 0, 1, 0, 1),
(2, 1, 1, 1, 'Set up a KBPublisher view', '<p>There are 3 different views for KBPublisher, Left Menu, Browsable and Intranet.<br />\r\nLeft Menu is set by default. See below.&nbsp;</p>\r\n\r\n<h3 class="lineTitle">Left Menu</h3>\r\n\r\n<p><img alt="" src="data:image/gif;base64," style="height: 296px; width: 400px;" /></p>\r\n\r\n<p></p>\r\n\r\n<h3 class="lineTitle">Intranet</h3>\r\n\r\n<p><img alt="" src="data:image/gif;base64," style="width: 400px; height: 296px;" /><br />\r\n&nbsp;</p>\r\n\r\n<h3 class="lineTitle">Browsable</h3>\r\n\r\n<p><img alt="" src="data:image/gif;base64," style="height: 296px; width: 400px;" /><br />\r\n&nbsp;</p>\r\n\r\n<h3 class="lineTitle">To change view</h3>\r\n\r\n<ul>\r\n	<li>Sign in to the knowledgebase</li>\r\n	<li>Go the&nbsp;<strong>Admin Area</strong></li>\r\n	<li>Click on the&nbsp;<strong>Settings</strong>&nbsp;menu and choose the&nbsp;<strong>Public Area&nbsp;-&gt; Common</strong>&nbsp;tab</li>\r\n	<li>Select required view in the&nbsp;<strong>View format</strong>&nbsp;field</li>\r\n	<li>For Left Menu and Intranet views you can specify which menu type to use, set&nbsp;<strong>Left Menu Type</strong></li>\r\n	<li>Click&nbsp;<strong>Save&nbsp;</strong>to save the changes</li>\r\n	<li>Go to&nbsp;<strong>Public Area</strong>&nbsp;to see/test your new settings<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<h3 class="lineTitle">To customize view</h3>\r\n\r\n<p>See below articles for details</p>\r\n\r\n<ul>\r\n	<li><a href="http://www.kbpublisher.com/kb/change-kbpublisher-look-and-feel-(design-integration)_217.html">Change KBPublisher Look and Feel</a></li>\r\n	<li><a href="http://www.kbpublisher.com/kb/customizing-article-display-options_101.html">Customizing article display options</a></li>\r\n	<li><a href="http://www.kbpublisher.com/kb/customizing-public-area_95.html">Customizing Public Area</a></li>\r\n</ul>\r\n', 'There are 3 different views for KBPublisher, Left Menu, Browsable and Intranet. Left Menu is set by default. See below. Left Menu Intranet Browsable To change view Sign in to the knowledgebase Go the Admin Area Click on the Settings menu and choose the Public Area -> Common tab Select required view in the View format field For Left Menu and Intranet views you can specify which menu type to use, set Left Menu Type Click Save to save the changes Go to Public Area to see/test your new settings To customize view See below articles for details Change KBPublisher Look and Feel Customizing article display options Customizing Public Area', '', '', '', 0, '', '2017-04-12 12:46:27', '2017-04-12 16:46:27', '0000-00-00 00:00:00', '', 0, 1, 0, 1),
(3, 1, 1, 1, 'Manuals, FAQ, HowTo', '<ul>\r\n	<li><a href="http://www.kbpublisher.com/kb/1/">User Manual</a></li>\r\n	<li><a href="http://www.kbpublisher.com/kb/50/">Developer Manual</a></li>\r\n	<li><a href="http://www.kbpublisher.com/kb/2/">FAQ</a></li>\r\n	<li><a href="http://www.kbpublisher.com/kb/55/">HowTo, Tips &amp; Tricks</a></li>\r\n</ul>\r\n', 'User Manual Developer Manual FAQ HowTo, Tips & Tricks', '', '', '', 0, '', '2017-04-12 12:46:27', '2017-04-12 16:46:27', '0000-00-00 00:00:00', NULL, 0, 1, 0, 1);

